mapper.select:
SELECT * FROM member
ORDER BY num
    LIMIT #{offset}, #{size}

mapper.update:
UPDATE member
SET pw = #{pw}, name = #{name}, tel = #{tel}, imgname = #{imgname}, like_categories = #{like_categories}, introduce = #{introduce}, imgPath = #{imgPath}
WHERE id = #{id}

mapper.select:
SELECT *
FROM pageon.member
WHERE name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY reg_date ${sortOrder}
LIMIT #{offset}, #{size}
