mapper.select:
SELECT * FROM member
ORDER BY num
    LIMIT #{offset}, #{size}

mapper.update:
UPDATE pageon.member
SET pw = #{pw}, name = #{name}, tel = #{tel}, img_name = #{img_name}, introduce = #{introduce}, img_data = #{img_data}
WHERE id = #{id}

mapper.select:
SELECT *
FROM pageon.member
WHERE name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY reg_date ${sortOrder}
LIMIT #{offset}, #{size}
