mapper.select:
SELECT *
FROM work
WHERE item_id = #{item_id}
  AND type = 'webtoon';

SELECT * FROM work WHERE categories IN
#{category}
AND type = 'webtoon' ORDER BY item_id DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM work WHERE categories IN
#{category}
AND type = 'webtoon'

mapper.update:
UPDATE work
SET update_day = #{update_day},
    link = #{link}
WHERE title = #{title}
  AND type = 'webtoon'
